import React from 'react';
import { useSelector } from 'react-redux';
import { RootState } from '../app/store';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../services/useAuth';

const DashboardPage: React.FC = () => {
  const user = useSelector((state: RootState) => state.auth.user);
  const navigate = useNavigate();
  const { logout } = useAuth();

  const handleLogout = () => {
    console.log('Logout button clicked');
    logout();
    navigate("/signin");
  };

  return (
    <div style={{ padding: '40px', fontFamily: 'Arial, sans-serif', backgroundColor: '#eef2f3', minHeight: '100vh' }}>
      <header style={{ backgroundColor: '#4a90e2', color: '#fff', padding: '20px', textAlign: 'center', borderRadius: '8px' }}>
        <h1 style={{ fontSize: '2.5rem', margin: '0' }}>Hello, {user?.name}!</h1>
        <h2 style={{ fontSize: '1.5rem', margin: '10px 0' }}>Your Dashboard Awaits</h2>
      </header>

      <section style={{ marginTop: '30px', backgroundColor: '#fff', padding: '20px', borderRadius: '8px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)' }}>
        <h3 style={{ fontSize: '1.8rem', color: '#333' }}>Recent Activity</h3>
        <ul style={{ listStyleType: 'circle', paddingLeft: '20px' }}>
          <li style={{ margin: '10px 0' }}>You logged in successfully!</li>
        </ul>
      </section>

      <section style={{ marginTop: '20px', backgroundColor: '#fff', padding: '20px', borderRadius: '8px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)' }}>
        <h3 style={{ fontSize: '1.8rem', color: '#333' }}>Your Tasks</h3>
        <ul style={{ listStyleType: 'square', paddingLeft: '20px' }}>
          <li style={{ margin: '10px 0' }}>Complete the onboarding process</li>
          <li style={{ margin: '10px 0' }}>Attend the team meeting at 3 PM</li>
          <li style={{ margin: '10px 0' }}>Submit project feedback</li>
        </ul>
      </section>

      <section style={{ marginTop: '20px', backgroundColor: '#fff', padding: '20px', borderRadius: '8px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)' }}>
        <h3 style={{ fontSize: '1.8rem', color: '#333' }}>Upcoming Events</h3>
        <ul style={{ listStyleType: 'circle', paddingLeft: '20px' }}>
          <li style={{ margin: '10px 0' }}>Project Kick-off - Next Monday</li>
          <li style={{ margin: '10px 0' }}>Team Outing - Next Friday</li>
        </ul>
      </section>

      {/* Logout button */}
      <button 
        onClick={handleLogout} 
        style={{ 
          backgroundColor: '#dc3545', 
          color: '#fff', 
          padding: '12px 20px', 
          border: 'none', 
          borderRadius: '5px', 
          cursor: 'pointer', 
          fontSize: '16px', 
          marginTop: '30px',
          transition: 'background-color 0.3s ease' 
        }}
        onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#c82333'}
        onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#dc3545'}
      >
        Logout
      </button>
    </div>
  );
};

export default DashboardPage;
